pub fn verse(n: i32) -> String {    if n > 2 {        format!("{0} bottles of beer on the wall, {0} bottles of beer.\nTake one down and pass it around, {1} bottles of beer on the wall.\n", n, n - 1).to_string()    } else if n == 2 {        format!("{0} bottles of beer on the wall, {0} bottles of beer.\nTake one down and pass it around, {1} bottle of beer on the wall.\n", n, n - 1).to_string()    } else if n == 1 {        format!("{0} bottle of beer on the wall, {0} bottle of beer.\nTake it down and pass it around, no more bottles of beer on the wall.\n", n).to_string()    } else {        "No more bottles of beer on the wall, no more bottles of beer.\nGo to the store and buy some more, 99 bottles of beer on the wall.\n".to_string()    }}pub fn sing(start: i32, end: i32) -> String {    let mut ret = String::new();    for i in (end+1..start+1).rev() {        ret += &verse(i);        ret.push_str("\n");    }    ret += &verse(end); /* add last verse outside of the loop because we don't want a new line at the end of the text */    ret}